import React, { useEffect, useState } from 'react';
import axios from 'axios'
import '../styles/CreatePost.css';
import { useNavigate, useParams } from 'react-router-dom';

export default function UpdatePost() {
  
  const {id} = useParams();
  const navigate = useNavigate();
    
  const [form, setForm] = useState({
    title : '',
    author: '',
    rating: '',
    review: '',
    date: ''
  });

  useEffect = ()=>{
    const fetchReview = async() =>{
        try {
            
        } catch (error) {
            
        }
    }
  }

  const handleChange = (e) =>
    setForm({...form, [e.target.name]: e.target.value});

  
  const handleSubmit = async(e) =>{
    e.preventDefault();

    try {
      await axios.put('http://localhost:5000/api/reviews/${id}',form);
      alert('Review updated successfully');
      navigate('/reviews'); // Redirect to the reviews page after updating
    } catch (error) {
      alert('Failed to update review. Try again.');
    }
  }




  return (
    <div className="create-post-container">
      <h1>Update a Book Review</h1>
      <form onSubmit={handleSubmit}>
        <label>Book Title:</label>
        <input type="text" placeholder="Enter book title"  value={form.title} onChange={handleChange}/>

        <label>Author:</label>
        <input type="text" placeholder="Author's name"  value={form.author} onChange={handleChange}/>

        <label>Rating:</label>
        <input type="text" placeholder="Rate from 1 to 5" value={form.rating} onChange={handleChange}/>

        <label>Review Text:</label>
        <input type="text" placeholder="Write your review" value={form.review} onChange={handleChange}/>

        <label>Date:</label>
        <input type="date"  value={form.date} onChange={handleChange}/>

        <button type="submit">Submit</button>
      </form>
    </div>
  );
}
