import React, { useState } from 'react';
import '../styles/CreatePost.css';

export default function CreatePost() {

  const [form, setForm] = useState({
    title : '',
    author: '',
    rating: '',
    review: '',
    date: ''
  });


  




  return (
    <div className="create-post-container">
      <h1>Create New Book Review</h1>
      <form on>
        <label>Book Title:</label>
        <input type="text" placeholder="Enter book title"  value={form.title}/>

        <label>Author:</label>
        <input type="text" placeholder="Author's name"  value={form.author}/>

        <label>Rating:</label>
        <input type="text" placeholder="Rate from 1 to 5" value={form.rating}/>

        <label>Review Text:</label>
        <input type="text" placeholder="Write your review" value={form.review}/>

        <label>Date:</label>
        <input type="date"  value={form.date}/>

        <button type="submit">Submit</button>
      </form>
    </div>
  );
}
