import React, { useState } from 'react';
import '../styles/CreatePost.css';

export default function CreatePost() {

  const [form, setForm] = useState({
    title : '',
    author: '',
    
  })
  




  return (
    <div className="create-post-container">
      <h1>Create New Book Review</h1>
      <form>
        <label>Book Title:</label>
        <input type="text" placeholder="Enter book title"  value={form.title}/>

        <label>Author:</label>
        <input type="text" placeholder="Author's name" />

        <label>Rating:</label>
        <input type="text" placeholder="Rate from 1 to 5" />

        <label>Review Text:</label>
        <input type="text" placeholder="Write your review" />

        <label>Date:</label>
        <input type="date" />

        <button type="submit">Submit</button>
      </form>
    </div>
  );
}
