import React, { useState } from 'react';
import axios from 'axios';
import '../styles/Register.css';
import { useNavigate } from 'react-router-dom';

export default function Login() {
  const [form, setForm] = useState({
    name: '',
    password: ''
  });

  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post(`http://localhost:5000/api/signin`, form);
      console.log("Response: ", response.data);
      alert("Registration Successful");
      setForm({ name: '', password: '' });
      navigate('/'); 
    } catch (error) {
      alert("Error, Try again!");
    }
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  return (
    <div className="register-container">
      <div className="register-card">
        <h1>SIGN In</h1>
        <form onSubmit={handleSubmit}>
          <label>Full Name:</label>
          <input
            type="text"
            placeholder="John Stev"
            name="name"
            required
            value={form.name}
            onChange={handleChange}
          />

         

          <label>Password:</label>
          <input
            type="password"
            placeholder="**********"
            name="password"
            required
            value={form.password}
            onChange={handleChange}
          />

          <button type="submit" className="signup-btn">Sign In</button>

          <br></br>

          <span>You haven't account yet?</span>
          <button type="button" className="signin-btn" onClick={() => navigate('/signup')}>
            Sign Up
          </button>
        </form>
      </div>
    </div>
  );
}
