import React, { useState } from 'react';
import axios from 'axios'
import '../styles/CreatePost.css';

export default function CreatePost() {

  const [form, setForm] = useState({
    title : '',
    author: '',
    rating: '',
    review: '',
    date: ''
  });


  const handleChange = (e) =>
    setForm({...form, [e.target.name]: e.target.value});

  
  const handleSubmit = async(e) =>{
    e.preventDefault();

    try {
      await axios.post(`http://localhost:5000/api/create`,form);
      alert("Review created successfully");
      setForm({title: '', author: '', rating: '', review: '', date: ''});
    } catch (error) {
      alert("Failed to create review. Try again.",error);
    }
  }




  return (
    <div className="create-post-container">
      <h1>Create New Book Review</h1>
      <form onSubmit={handleSubmit}>
        <label>Book Title:</label>
        <input type="text" placeholder="Enter book title"  value={form.title} onChange={handleChange} required/>

        <label>Author:</label>
        <input type="text" placeholder="Author's name"  value={form.author} onChange={handleChange} required/>

        <label>Rating:</label>
        <input type="text" placeholder="Rate from 1 to 5" min="1" max="5" value={form.rating} onChange={handleChange} required/>

        <label>Review Text:</label>
        <input type="text" placeholder="Write your review" value={form.review} onChange={handleChange}/>

        <label>Date:</label>
        <input type="date"  value={form.date} onChange={handleChange}/>

        <button type="submit">Submit</button>
      </form>
    </div>
  );
}
